import React from 'react';
import { useTranslation } from 'react-i18next';
import { trackButtonClick, trackConsultationClick } from '../../utils/analytics';

const HeroSection: React.FC = () => {
  const { t } = useTranslation();

  return (
    <section className="py-32 bg-white relative overflow-hidden">
      {/* Subtle background pattern */}
      <div className="absolute inset-0 opacity-[0.02]">
        <div className="absolute inset-0" style={{
          backgroundImage: 'radial-gradient(circle at 2px 2px, #0f172a 1px, transparent 0)',
          backgroundSize: '40px 40px'
        }}></div>
      </div>

      <div className="max-w-5xl mx-auto px-6 relative">
        <div className="text-center max-w-3xl mx-auto">
          <h1 className="text-6xl md:text-7xl font-semibold text-slate-900 mb-8 leading-tight tracking-tight animate-fade-in">
            {t('hero.title')}
          </h1>
          <p className="text-xl md:text-2xl text-slate-600 leading-relaxed mb-6 max-w-2xl mx-auto">
            {t('hero.subtitle')}
          </p>
          <p className="text-lg text-slate-500 mb-12 max-w-2xl mx-auto">
            {t('hero.subtitle2')}
          </p>

          <div className="flex flex-col sm:flex-row gap-3 justify-center mb-16">
            <a 
              href="#pricing" 
              className="button-primary"
              onClick={() => trackButtonClick('Start Free Trial', 'Hero Section')}
            >
              {t('hero.startFreeTrial')}
            </a>
            <a 
              href="https://calendly.com/fynteq/30min" 
              target="_blank" 
              rel="noopener noreferrer" 
              className="button-secondary"
              onClick={() => trackConsultationClick('Hero CTA')}
            >
              {t('hero.bookCall')}
            </a>
          </div>

          {/* Payment Provider Logos - Minimal */}
          <div className="flex flex-wrap justify-center items-center gap-6 text-sm text-slate-500">
            <span>Stripe</span>
            <span>·</span>
            <span>PayPal</span>
            <span>·</span>
            <span>Shopify</span>
            <span>·</span>
            <span>Apple Pay</span>
            <span>·</span>
            <span>Google Pay</span>
            <span>·</span>
            <span>Adyen</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
