import React from 'react';
import { useTranslation } from 'react-i18next';

const ServicesSection: React.FC = () => {
  const { t } = useTranslation();

  return (
    <section id="services" className="section-spacing bg-slate-50 relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-r from-slate-100/40 via-transparent to-slate-100/40"></div>
      <div className="max-w-7xl mx-auto px-6 relative">
        <div className="text-center mb-20">
          <h2 className="heading-lg text-slate-900 mb-6 text-balance">
            {t('services.title')}
          </h2>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-16">
          {t('services.features', { returnObjects: true }).map((feature: any, index: number) => (
            <div key={index} className="card p-8 hover-lift relative overflow-hidden">
              <div className="absolute top-0 right-0 w-16 h-16 bg-gradient-to-br from-blue-400/10 to-blue-600/10 rounded-full -translate-y-8 translate-x-8"></div>
              {feature.icon && (
                <div className="w-12 h-12 bg-gradient-to-br from-blue-100 to-blue-200 rounded-lg flex items-center justify-center mb-6">
                  <span className="text-2xl">{feature.icon}</span>
                </div>
              )}
              <h3 className="text-xl font-semibold text-slate-900 mb-3">{feature.title}</h3>
              <p className="text-slate-600 leading-relaxed mb-4">{feature.description}</p>
              {feature.items && (
                <ul className="space-y-2">
                  {feature.items.map((item: string, itemIndex: number) => (
                    <li key={itemIndex} className="flex items-start text-sm text-slate-600">
                      <svg className="w-4 h-4 text-green-500 mr-2 flex-shrink-0 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd"/>
                      </svg>
                      <span>{item}</span>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          ))}
        </div>

        <div className="mt-12 text-center">
          <h3 className="text-xl font-semibold text-slate-900 mb-6">{t('services.integrations.title')}</h3>
          <div className="flex flex-wrap justify-center gap-4 text-slate-600">
            {t('services.integrations.list', { returnObjects: true }).map((integration: string, index: number) => (
              <span key={index} className="px-4 py-2 bg-white rounded-lg border border-slate-200 font-medium">
                {integration}
              </span>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default ServicesSection;
