import React from 'react';
import { useTranslation } from 'react-i18next';

const ProcessSection: React.FC = () => {
  const { t } = useTranslation();
  const steps = t('process.steps', { returnObjects: true }) as Array<{
    number: string;
    title: string;
    description: string;
    items?: string[];
  }>;

  return (
    <section className="section-spacing bg-white relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-r from-slate-50/30 via-transparent to-slate-50/30"></div>
      <div className="max-w-7xl mx-auto px-6 relative">
        <div className="text-center mb-16">
          <h2 className="heading-lg text-slate-900 mb-6 text-balance">
            {t('process.title')}
          </h2>
          <p className="text-lg text-slate-600 max-w-2xl mx-auto text-balance">
            {t('process.subtitle')}
          </p>
        </div>

        <div className="space-y-12 max-w-4xl mx-auto">
          {steps.map((step, index) => (
            <div key={index} className="card p-8 hover-lift">
              <div className="flex items-start space-x-6">
                <div className="flex-shrink-0">
                  <div className="w-16 h-16 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-full flex items-center justify-center text-2xl font-bold">
                    {step.number}
                  </div>
                </div>
                <div className="flex-1">
                  <h3 className="text-2xl font-semibold text-slate-900 mb-4">{step.title}</h3>
                  <p className="text-slate-600 leading-relaxed mb-4">{step.description}</p>
                  {step.items && (
                    <ul className="space-y-2 mt-4">
                      {step.items.map((item: string, itemIndex: number) => (
                        <li key={itemIndex} className="flex items-start text-slate-600">
                          <svg className="w-5 h-5 text-blue-600 mr-2 flex-shrink-0 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
                            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd"/>
                          </svg>
                          <span>{item}</span>
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="mt-16 text-center">
          <p className="text-lg text-slate-700 font-semibold mb-4">{t('process.noDevelopers')}</p>
        </div>
      </div>
    </section>
  );
};

export default ProcessSection;
