import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { ROUTES } from '../utils/routing';

// Import all page components
import Home from '../pages/Home';
import Privacy from '../pages/Privacy';
import Terms from '../pages/Terms';
import Cookies from '../pages/Cookies';
import Refund from '../pages/Refund';
import Security from '../pages/Security';
import StarterForm from '../pages/StarterForm';
import GrowthForm from '../pages/GrowthForm';
import ScaleForm from '../pages/ScaleForm';
import Imprint from '../pages/Imprint';
import Blog from '../pages/Blog';
import BlogPost from '../pages/BlogPost';
import CaseStudies from '../pages/CaseStudies';
import CaseStudyDetail from '../pages/CaseStudyDetail';

// Language-aware route component
const LanguageAwareRoute: React.FC<{
  path: string;
  element: React.ReactElement;
  isGerman?: boolean;
}> = ({ path, element, isGerman = false }) => {
  const { i18n } = useTranslation();

  // Set language based on route
  React.useEffect(() => {
    const targetLanguage = isGerman ? 'de' : 'en';
    if (i18n.language !== targetLanguage) {
      i18n.changeLanguage(targetLanguage);
    }
  }, [isGerman, i18n]);

  return <Route path={path} element={element} />;
};

// Main routes component
const LanguageAwareRoutes: React.FC = () => {
  return (
    <Routes>
      {/* English routes */}
      <LanguageAwareRoute path={ROUTES.HOME} element={<Home />} />
      <LanguageAwareRoute path={ROUTES.PRIVACY} element={<Privacy />} />
      <LanguageAwareRoute path={ROUTES.TERMS} element={<Terms />} />
      <LanguageAwareRoute path={ROUTES.COOKIES} element={<Cookies />} />
      <LanguageAwareRoute path={ROUTES.REFUND} element={<Refund />} />
      <LanguageAwareRoute path={ROUTES.SECURITY} element={<Security />} />
      <LanguageAwareRoute path={ROUTES.STARTER_FORM} element={<StarterForm />} />
      <LanguageAwareRoute path={ROUTES.GROWTH_FORM} element={<GrowthForm />} />
      <LanguageAwareRoute path={ROUTES.SCALE_FORM} element={<ScaleForm />} />
      <LanguageAwareRoute path={ROUTES.IMPRINT} element={<Imprint />} />
      <LanguageAwareRoute path={ROUTES.BLOG} element={<Blog />} />
      <LanguageAwareRoute path={ROUTES.BLOG_POST} element={<BlogPost />} />
      <LanguageAwareRoute path={ROUTES.CASE_STUDIES} element={<CaseStudies />} />
      <LanguageAwareRoute path={ROUTES.CASE_STUDY_DETAIL} element={<CaseStudyDetail />} />

      {/* German routes */}
      <LanguageAwareRoute path="/de" element={<Home />} isGerman />
      <LanguageAwareRoute path="/de/privacy" element={<Privacy />} isGerman />
      <LanguageAwareRoute path="/de/terms" element={<Terms />} isGerman />
      <LanguageAwareRoute path="/de/cookies" element={<Cookies />} isGerman />
      <LanguageAwareRoute path="/de/refund" element={<Refund />} isGerman />
      <LanguageAwareRoute path="/de/security" element={<Security />} isGerman />
      <LanguageAwareRoute path="/de/starter-form" element={<StarterForm />} isGerman />
      <LanguageAwareRoute path="/de/growth-form" element={<GrowthForm />} isGerman />
      <LanguageAwareRoute path="/de/scale-form" element={<ScaleForm />} isGerman />
      <LanguageAwareRoute path="/de/imprint" element={<Imprint />} isGerman />
      <LanguageAwareRoute path="/de/blog" element={<Blog />} isGerman />
      <LanguageAwareRoute path="/de/blog/:slug" element={<BlogPost />} isGerman />
      <LanguageAwareRoute path="/de/case-studies" element={<CaseStudies />} isGerman />
      <LanguageAwareRoute path="/de/case-studies/:slug" element={<CaseStudyDetail />} isGerman />
    </Routes>
  );
};

export default LanguageAwareRoutes;
