// Generate language-aware URL
export const getLocalizedUrl = (path: string, currentLanguage: string): string => {
  // Handle empty path or root path
  if (path === '' || path === '/') {
    return currentLanguage === 'de' ? '/de' : '/';
  }

  // Remove leading slash if present
  const cleanPath = path.startsWith('/') ? path.substring(1) : path;

  // Add language prefix for German
  if (currentLanguage === 'de') {
    return `/de/${cleanPath}`;
  }

  // For English, return path as-is (with leading slash)
  return `/${cleanPath}`;
};

// Get current language from URL
export const getLanguageFromUrl = (pathname: string): string => {
  return pathname.startsWith('/de') ? 'de' : 'en';
};

// Remove language prefix from URL
export const removeLanguagePrefix = (pathname: string): string => {
  if (pathname.startsWith('/de')) {
    const withoutPrefix = pathname.substring(3);
    return withoutPrefix === '' ? '/' : withoutPrefix;
  }
  return pathname;
};
