import React from 'react';
import { useTranslation } from 'react-i18next';
import { trackButtonClick, trackConsultationClick } from '../../utils/analytics';

const HeroSection: React.FC = () => {
  const { t } = useTranslation();

  return (
    <section className="section-spacing bg-white relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-br from-blue-50/50 via-transparent to-purple-50/50"></div>
      <div className="max-w-6xl mx-auto px-6 relative">
        <div className="text-center max-w-4xl mx-auto">
          <h1 className="heading-xl text-slate-900 mb-6 text-balance">
            <span className="gradient-text">{t('hero.title')}</span>
          </h1>
          <p className="text-xl text-slate-600 leading-relaxed text-balance mb-8 max-w-3xl mx-auto">
            {t('hero.subtitle')}
          </p>
          <p className="text-lg text-slate-700 leading-relaxed text-balance mb-12 max-w-3xl mx-auto font-medium">
            {t('hero.subtitle2')}
          </p>

          <div className="flex flex-col sm:flex-row gap-4 justify-center mb-12">
            <a 
              href="#pricing" 
              className="button-primary"
              onClick={() => trackButtonClick('Start Free Trial', 'Hero Section')}
            >
              {t('hero.startFreeTrial')}
            </a>
            <a 
              href="https://calendly.com/fynteq/30min" 
              target="_blank" 
              rel="noopener noreferrer" 
              className="button-secondary"
              onClick={() => trackConsultationClick('Hero CTA')}
            >
              {t('hero.bookCall')}
            </a>
          </div>

          <p className="text-sm text-slate-500 mb-8 text-center">
            {t('hero.trustedFor')}
          </p>

          {/* Payment Provider Logos */}
          <div className="flex flex-wrap justify-center items-center gap-6 md:gap-8 mb-16">
            <span className="text-slate-600 font-medium">Stripe</span>
            <span className="text-slate-400">·</span>
            <span className="text-slate-600 font-medium">PayPal</span>
            <span className="text-slate-400">·</span>
            <span className="text-slate-600 font-medium">Shopify</span>
            <span className="text-slate-400">·</span>
            <span className="text-slate-600 font-medium">Apple Pay</span>
            <span className="text-slate-400">·</span>
            <span className="text-slate-600 font-medium">Google Pay</span>
            <span className="text-slate-400">·</span>
            <span className="text-slate-600 font-medium">Adyen</span>
          </div>

        </div>
      </div>
    </section>
  );
};

export default HeroSection;
