import React from 'react';
import { useTranslation } from 'react-i18next';

const PainPointsSection: React.FC = () => {
  const { t } = useTranslation();

  return (
    <section className="py-24 bg-white">
      <div className="max-w-6xl mx-auto px-6">
        <div className="text-center mb-16">
          <h2 className="heading-lg text-slate-900 mb-6 text-balance">
            {t('painPoints.title')}
          </h2>
          <p className="text-lg text-slate-600 max-w-3xl mx-auto text-balance mb-8">
            {t('painPoints.subtitle')}
          </p>
          {t('painPoints.subtitle2') && (
            <p className="text-base text-slate-700 max-w-3xl mx-auto text-balance font-medium">
              {t('painPoints.subtitle2')}
            </p>
          )}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto">
          {t('painPoints.problems', { returnObjects: true }).map((problem: string, index: number) => (
            <div key={index} className="bg-white rounded-xl p-6 border border-red-100 shadow-sm">
              <div className="flex items-start space-x-3">
                <div className="w-6 h-6 bg-red-100 rounded-full flex items-center justify-center flex-shrink-0 mt-0.5">
                  <svg className="w-4 h-4 text-red-600" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd"/>
                  </svg>
                </div>
                <p className="text-slate-700 leading-relaxed">{problem}</p>
              </div>
            </div>
          ))}
        </div>

        <div className="mt-12 text-center">
          <p className="text-lg text-slate-800 font-semibold mb-2">{t('painPoints.conclusion')}</p>
          {t('painPoints.conclusion2') && (
            <p className="text-base text-slate-600">{t('painPoints.conclusion2')}</p>
          )}
        </div>
      </div>
    </section>
  );
};

export default PainPointsSection;
