import React from 'react';
import { useTranslation } from 'react-i18next';

const PainPointsSection: React.FC = () => {
  const { t } = useTranslation();

  const statistics = t('painPoints.statistics', { returnObjects: true }) as Array<{stat: string, description: string}>;
  const consequences = t('painPoints.ifNotMonitoring.consequences', { returnObjects: true }) as string[];

  return (
    <section className="py-24 bg-white">
      <div className="max-w-5xl mx-auto px-6">
        <div className="text-center mb-20">
          <h2 className="text-4xl md:text-5xl font-semibold text-slate-900 mb-6 leading-tight">
            {t('painPoints.title')}
          </h2>
          <p className="text-lg text-slate-600 max-w-2xl mx-auto">
            {t('painPoints.subtitle')}
          </p>
        </div>

        {/* Statistics - Minimal List */}
        <div className="space-y-4 mb-16 max-w-3xl mx-auto">
          {statistics.map((stat: any, index: number) => (
            <div key={index} className="flex items-start space-x-4 py-4 border-b border-slate-100 last:border-0">
              <div className="text-2xl font-semibold text-slate-900 min-w-[80px]">{stat.stat}</div>
              <p className="text-base text-slate-600 pt-1">{stat.description}</p>
            </div>
          ))}
        </div>

        {/* If Not Monitoring - Simple List */}
        <div className="max-w-3xl mx-auto mb-16">
          <h3 className="text-xl font-semibold text-slate-900 mb-6">
            {t('painPoints.ifNotMonitoring.title')}
          </h3>
          <div className="space-y-3">
            {consequences.map((consequence: string, index: number) => (
              <div key={index} className="flex items-center space-x-3">
                <div className="w-1.5 h-1.5 rounded-full bg-slate-400"></div>
                <p className="text-slate-700">{consequence}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Conclusion */}
        <div className="text-center max-w-2xl mx-auto">
          <p className="text-lg text-slate-900 font-medium">{t('painPoints.conclusion')}</p>
        </div>
      </div>
    </section>
  );
};

export default PainPointsSection;
