import React, { useEffect } from 'react';
import { BrowserRouter as Router, useLocation } from 'react-router-dom';
import { HelmetProvider } from 'react-helmet-async';
import { initGA, trackPageView } from './utils/analytics';
import { detectUserLanguage, redirectToLanguage } from './utils/geoLocation';
import './i18n';
import Header from './components/Header';
import Footer from './components/Footer';
import ChatWidget from './components/ChatWidget';
import CookieConsent from './components/CookieConsent';
import LanguageAwareRoutes from './components/LanguageAwareRoutes';

// Component to track page views
const PageTracker: React.FC = () => {
  const location = useLocation();

  useEffect(() => {
    trackPageView(location.pathname + location.search);
  }, [location]);

  return null;
};


function App() {
  useEffect(() => {
    initGA();

    // Auto-detect language based on user's location (only if not already on a language route)
    const checkLanguage = async () => {
      const currentPath = window.location.pathname;
      // Don't auto-redirect if user is already on a language-specific route
      if (!currentPath.startsWith('/de') && currentPath !== '/de') {
        const userLanguage = await detectUserLanguage();
        redirectToLanguage(userLanguage);
      }
    };

    checkLanguage();
  }, []);

  return (
    <HelmetProvider>
      <Router>
        <div className="App">
          <PageTracker />
          <Header />
          <main>
            <LanguageAwareRoutes />
          </main>
          <Footer />
          <ChatWidget />
          <CookieConsent />
        </div>
      </Router>
    </HelmetProvider>
  );
}

export default App;
