import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

// Route definitions
export const ROUTES = {
  HOME: '/',
  PRIVACY: '/privacy',
  TERMS: '/terms',
  COOKIES: '/cookies',
  REFUND: '/refund',
  SECURITY: '/security',
  STARTER_FORM: '/starter-form',
  GROWTH_FORM: '/growth-form',
  SCALE_FORM: '/scale-form',
  IMPRINT: '/imprint',
  BLOG: '/blog',
  BLOG_POST: '/blog/:slug',
  CASE_STUDIES: '/case-studies',
  CASE_STUDY_DETAIL: '/case-studies/:slug',
} as const;

// Language-aware routing utilities
export const useLanguageRouting = () => {
  const location = useLocation();
  const navigate = useNavigate();
  useTranslation(); // Initialize i18n context

  // Get current language from URL
  const getCurrentLanguage = (): string => {
    return location.pathname.startsWith('/de') ? 'de' : 'en';
  };

  // Check if current route is German
  const isGerman = (): boolean => {
    return getCurrentLanguage() === 'de';
  };

  // Get localized path
  const getLocalizedPath = (path: string, language?: string): string => {
    const targetLanguage = language || getCurrentLanguage();
    const cleanPath = path.startsWith('/') ? path.substring(1) : path;

    if (targetLanguage === 'de') {
      return `/de/${cleanPath}`;
    }

    return `/${cleanPath}`;
  };

  // Navigate to localized path
  const navigateToLocalized = (path: string, language?: string) => {
    const localizedPath = getLocalizedPath(path, language);
    navigate(localizedPath);
  };

  // Get path without language prefix
  const getPathWithoutLanguage = (): string => {
    const pathname = location.pathname;
    if (pathname.startsWith('/de')) {
      const withoutPrefix = pathname.substring(3);
      return withoutPrefix === '' ? '/' : withoutPrefix;
    }
    return pathname;
  };

  // Switch language while keeping current path
  const switchLanguage = (newLanguage: string) => {
    // Save language preference in cookie
    document.cookie = `language=${newLanguage}; path=/; max-age=31536000`; // 1 year
    const currentPath = getPathWithoutLanguage();
    const newPath = getLocalizedPath(currentPath, newLanguage);
    navigate(newPath);
  };

  // Get localized route for a specific route key
  const getLocalizedRoute = (routeKey: keyof typeof ROUTES, language?: string): string => {
    const route = ROUTES[routeKey];
    return getLocalizedPath(route, language);
  };

  return {
    getCurrentLanguage,
    isGerman,
    getLocalizedPath,
    navigateToLocalized,
    getPathWithoutLanguage,
    switchLanguage,
    getLocalizedRoute,
    currentLanguage: getCurrentLanguage(),
  };
};

// Hook for language-aware navigation
export const useLocalizedNavigation = () => {
  const { navigateToLocalized, getLocalizedRoute, switchLanguage } = useLanguageRouting();

  return {
    navigateTo: navigateToLocalized,
    getRoute: getLocalizedRoute,
    switchLanguage,
  };
};

// Component wrapper for language-aware routing
export const withLanguageRouting = <P extends object>(
  Component: React.ComponentType<P>
): React.ComponentType<P> => {
  const WrappedComponent: React.FC<P> = (props: P) => {
    const { i18n } = useTranslation();
    const { getCurrentLanguage } = useLanguageRouting();

    // Set language based on URL
    React.useEffect(() => {
      const currentLanguage = getCurrentLanguage();
      if (i18n.language !== currentLanguage) {
        i18n.changeLanguage(currentLanguage);
      }
    }, [getCurrentLanguage, i18n]);

    return React.createElement(Component, props);
  };

  return WrappedComponent;
};
