import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { ROUTES } from '../utils/routing';

// Import all page components
import Home from '../pages/Home';
import Privacy from '../pages/Privacy';
import Terms from '../pages/Terms';
import Cookies from '../pages/Cookies';
import Refund from '../pages/Refund';
import Security from '../pages/Security';
import StarterForm from '../pages/StarterForm';
import GrowthForm from '../pages/GrowthForm';
import ScaleForm from '../pages/ScaleForm';
import Imprint from '../pages/Imprint';
import Blog from '../pages/Blog';
import BlogPost from '../pages/BlogPost';
import CaseStudies from '../pages/CaseStudies';
import CaseStudyDetail from '../pages/CaseStudyDetail';

// Language-aware wrapper component
const LanguageAwareWrapper: React.FC<{
  children: React.ReactElement;
  isGerman?: boolean;
}> = ({ children, isGerman = false }) => {
  const { i18n } = useTranslation();
  const [isLanguageSet, setIsLanguageSet] = React.useState(false);

  // Get language preference from cookie
  const getLanguageFromCookie = (): string => {
    const cookies = document.cookie.split(';');
    for (let cookie of cookies) {
      const [name, value] = cookie.trim().split('=');
      if (name === 'language') {
        return value === 'de' ? 'de' : 'en';
      }
    }
    return 'en';
  };

  // Set language based on route and cookie preference
  React.useEffect(() => {
    const cookieLanguage = getLanguageFromCookie();
    const targetLanguage = isGerman ? 'de' : 'en';

    // If user has a cookie preference, use it; otherwise use route-based language
    const finalLanguage = cookieLanguage || targetLanguage;

    if (i18n.language !== finalLanguage) {
      i18n.changeLanguage(finalLanguage).then(() => {
        setIsLanguageSet(true);
      });
    } else {
      setIsLanguageSet(true);
    }
  }, [isGerman, i18n]);

  // Don't render until language is set
  if (!isLanguageSet) {
    return <div>Loading...</div>;
  }

  return children;
};

// Main routes component
const LanguageAwareRoutes: React.FC = () => {
  return (
    <Routes>
      {/* English routes */}
      <Route path={ROUTES.HOME} element={<LanguageAwareWrapper><Home /></LanguageAwareWrapper>} />
      <Route path={ROUTES.PRIVACY} element={<LanguageAwareWrapper><Privacy /></LanguageAwareWrapper>} />
      <Route path={ROUTES.TERMS} element={<LanguageAwareWrapper><Terms /></LanguageAwareWrapper>} />
      <Route path={ROUTES.COOKIES} element={<LanguageAwareWrapper><Cookies /></LanguageAwareWrapper>} />
      <Route path={ROUTES.REFUND} element={<LanguageAwareWrapper><Refund /></LanguageAwareWrapper>} />
      <Route path={ROUTES.SECURITY} element={<LanguageAwareWrapper><Security /></LanguageAwareWrapper>} />
      <Route path={ROUTES.STARTER_FORM} element={<LanguageAwareWrapper><StarterForm /></LanguageAwareWrapper>} />
      <Route path={ROUTES.GROWTH_FORM} element={<LanguageAwareWrapper><GrowthForm /></LanguageAwareWrapper>} />
      <Route path={ROUTES.SCALE_FORM} element={<LanguageAwareWrapper><ScaleForm /></LanguageAwareWrapper>} />
      <Route path={ROUTES.IMPRINT} element={<LanguageAwareWrapper><Imprint /></LanguageAwareWrapper>} />
      <Route path={ROUTES.BLOG} element={<LanguageAwareWrapper><Blog /></LanguageAwareWrapper>} />
      <Route path={ROUTES.BLOG_POST} element={<LanguageAwareWrapper><BlogPost /></LanguageAwareWrapper>} />
      <Route path={ROUTES.CASE_STUDIES} element={<LanguageAwareWrapper><CaseStudies /></LanguageAwareWrapper>} />
      <Route path={ROUTES.CASE_STUDY_DETAIL} element={<LanguageAwareWrapper><CaseStudyDetail /></LanguageAwareWrapper>} />

      {/* German routes */}
      <Route path="/de" element={<LanguageAwareWrapper isGerman><Home /></LanguageAwareWrapper>} />
      <Route path="/de/privacy" element={<LanguageAwareWrapper isGerman><Privacy /></LanguageAwareWrapper>} />
      <Route path="/de/terms" element={<LanguageAwareWrapper isGerman><Terms /></LanguageAwareWrapper>} />
      <Route path="/de/cookies" element={<LanguageAwareWrapper isGerman><Cookies /></LanguageAwareWrapper>} />
      <Route path="/de/refund" element={<LanguageAwareWrapper isGerman><Refund /></LanguageAwareWrapper>} />
      <Route path="/de/security" element={<LanguageAwareWrapper isGerman><Security /></LanguageAwareWrapper>} />
      <Route path="/de/starter-form" element={<LanguageAwareWrapper isGerman><StarterForm /></LanguageAwareWrapper>} />
      <Route path="/de/growth-form" element={<LanguageAwareWrapper isGerman><GrowthForm /></LanguageAwareWrapper>} />
      <Route path="/de/scale-form" element={<LanguageAwareWrapper isGerman><ScaleForm /></LanguageAwareWrapper>} />
      <Route path="/de/imprint" element={<LanguageAwareWrapper isGerman><Imprint /></LanguageAwareWrapper>} />
      <Route path="/de/blog" element={<LanguageAwareWrapper isGerman><Blog /></LanguageAwareWrapper>} />
      <Route path="/de/blog/:slug" element={<LanguageAwareWrapper isGerman><BlogPost /></LanguageAwareWrapper>} />
      <Route path="/de/case-studies" element={<LanguageAwareWrapper isGerman><CaseStudies /></LanguageAwareWrapper>} />
      <Route path="/de/case-studies/:slug" element={<LanguageAwareWrapper isGerman><CaseStudyDetail /></LanguageAwareWrapper>} />
    </Routes>
  );
};

export default LanguageAwareRoutes;
