import React from 'react';
import { useTranslation } from 'react-i18next';

const ServicesSection: React.FC = () => {
  const { t } = useTranslation();

  return (
    <section id="services" className="py-24 bg-white">
      <div className="max-w-6xl mx-auto px-6">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-semibold text-slate-900 mb-6 leading-tight">
            {t('services.title')}
          </h2>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-16">
          {t('services.features', { returnObjects: true }).map((feature: any, index: number) => (
            <div key={index} className="card p-8 group">
              <div className="w-12 h-12 bg-slate-100 rounded-xl flex items-center justify-center mb-4 group-hover:bg-slate-900 transition-colors">
                <svg className="w-6 h-6 text-slate-600 group-hover:text-white transition-colors" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <h3 className="text-xl font-semibold text-slate-900 mb-3">{feature.title}</h3>
              <p className="text-slate-600 leading-relaxed mb-4">{feature.description}</p>
              {feature.items && (
                <ul className="space-y-2">
                  {feature.items.map((item: string, itemIndex: number) => (
                    <li key={itemIndex} className="text-sm text-slate-600">
                      {item}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          ))}
        </div>

        <div className="text-center pt-8">
          <p className="text-sm font-medium text-slate-500 mb-6">Integrations</p>
          <div className="flex flex-wrap justify-center gap-3">
            {t('services.integrations.list', { returnObjects: true }).map((integration: string, index: number) => (
              <span 
                key={index}
                className="px-4 py-2 bg-slate-50 border border-slate-100 rounded-lg text-sm text-slate-600 hover:bg-slate-100 hover:border-slate-200 transition-colors"
              >
                {integration}
              </span>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default ServicesSection;
