import React from 'react';
import { useLanguageRouting } from '../utils/routing';

const LanguageSwitcher: React.FC = () => {
  const { switchLanguage, currentLanguage } = useLanguageRouting();

  const toggleLanguage = () => {
    const newLanguage = currentLanguage === 'en' ? 'de' : 'en';
    // Save language preference in cookie
    document.cookie = `language=${newLanguage}; path=/; max-age=31536000`; // 1 year
    switchLanguage(newLanguage);
  };

  const oppositeLanguage = currentLanguage === 'en' ? 'DE' : 'EN';

  return (
    <button
      onClick={toggleLanguage}
      className="px-3 py-1 rounded-full text-sm font-medium bg-slate-100 text-slate-700 hover:bg-slate-200 transition-colors"
    >
      {oppositeLanguage}
    </button>
  );
};

export default LanguageSwitcher;
