import React from 'react';
import { useTranslation } from 'react-i18next';

const PainPointsSection: React.FC = () => {
  const { t } = useTranslation();

  const statistics = t('painPoints.statistics', { returnObjects: true }) as Array<{stat: string, description: string}>;
  const consequences = t('painPoints.ifNotMonitoring.consequences', { returnObjects: true }) as string[];

  // Helper to extract percentage for visual representation
  const getPercentage = (stat: string): number => {
    const match = stat.match(/(\d+)[–-](\d+)/);
    if (match) {
      return (parseInt(match[1]) + parseInt(match[2])) / 2;
    }
    if (stat.includes('1 in 20')) return 5;
    return 0;
  };

  return (
    <section className="py-24 bg-white">
      <div className="max-w-6xl mx-auto px-6">
        <div className="mb-16">
          <h2 className="text-4xl md:text-5xl font-semibold text-slate-900 mb-4 leading-tight">
            {t('painPoints.title')}
          </h2>
          <p className="text-lg text-slate-500">
            {t('painPoints.subtitle')}
          </p>
        </div>

        {/* Statistics - Minimal Cards with Charts */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-16">
          {statistics.map((stat: any, index: number) => {
            const percentage = getPercentage(stat.stat);
            return (
              <div key={index} className="p-5 border border-slate-200 rounded-lg hover:border-slate-300 transition-colors">
                {/* Simple Line Chart */}
                <div className="mb-4">
                  <div className="h-1 bg-slate-100 rounded-full overflow-hidden">
                    <div 
                      className="h-full bg-slate-900 rounded-full transition-all"
                      style={{ width: `${Math.min(percentage, 100)}%` }}
                    ></div>
                  </div>
                </div>
                <div className="text-2xl font-semibold text-slate-900 mb-1">{stat.stat}</div>
                <p className="text-sm text-slate-600">{stat.description}</p>
              </div>
            );
          })}
        </div>

        {/* If Not Monitoring - Minimal Cards */}
        <div className="mb-16">
          <h3 className="text-xl font-semibold text-slate-900 mb-6">
            {t('painPoints.ifNotMonitoring.title')}
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {consequences.map((consequence: string, index: number) => (
              <div key={index} className="p-4 border border-slate-200 rounded-lg text-center">
                <p className="text-sm text-slate-700">{consequence}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Conclusion */}
        <div className="text-center">
          <p className="text-lg text-slate-900 font-medium">{t('painPoints.conclusion')}</p>
        </div>
      </div>
    </section>
  );
};

export default PainPointsSection;
