import React from 'react';
import { useTranslation } from 'react-i18next';

const PainPointsSection: React.FC = () => {
  const { t } = useTranslation();

  return (
    <section className="py-24 bg-slate-50">
      <div className="max-w-6xl mx-auto px-6">
        <div className="text-center mb-20">
          <h2 className="text-4xl md:text-5xl font-semibold text-slate-900 mb-6 leading-tight">
            {t('painPoints.title')}
          </h2>
          <p className="text-lg text-slate-600 max-w-2xl mx-auto mb-12">
            {t('painPoints.subtitle')}
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 max-w-4xl mx-auto mb-16">
          {t('painPoints.problems', { returnObjects: true }).map((problem: string, index: number) => (
            <div key={index} className="p-6 border border-slate-200 rounded-lg hover:border-slate-300 transition-colors">
              <p className="text-slate-700 leading-relaxed">{problem}</p>
            </div>
          ))}
        </div>

        <div className="text-center">
          <p className="text-lg text-slate-900 font-medium">{t('painPoints.conclusion')}</p>
          {t('painPoints.conclusion2') && (
            <p className="text-base text-slate-600 mt-2">{t('painPoints.conclusion2')}</p>
          )}
        </div>
      </div>
    </section>
  );
};

export default PainPointsSection;
