import React from 'react';
import { useTranslation } from 'react-i18next';

const PainPointsSection: React.FC = () => {
  const { t } = useTranslation();

  const statistics = t('painPoints.statistics', { returnObjects: true }) as Array<{stat: string, description: string}>;
  const consequences = t('painPoints.ifNotMonitoring.consequences', { returnObjects: true }) as string[];

  return (
    <section className="py-24 bg-slate-50">
      <div className="max-w-6xl mx-auto px-6">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-semibold text-slate-900 mb-6 leading-tight">
            {t('painPoints.title')}
          </h2>
          <p className="text-lg text-slate-600 max-w-2xl mx-auto mb-12">
            {t('painPoints.subtitle')}
          </p>
        </div>

        {/* Statistics Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-16 max-w-5xl mx-auto">
          {statistics.map((stat: any, index: number) => (
            <div key={index} className="p-6 border border-slate-200 rounded-lg bg-white hover:border-slate-300 transition-colors">
              <div className="text-3xl font-semibold text-slate-900 mb-2">{stat.stat}</div>
              <p className="text-sm text-slate-600 leading-relaxed">{stat.description}</p>
            </div>
          ))}
        </div>

        {/* If Not Monitoring Section */}
        <div className="max-w-4xl mx-auto mb-16">
          <h3 className="text-2xl font-semibold text-slate-900 mb-6 text-center">
            {t('painPoints.ifNotMonitoring.title')}
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {consequences.map((consequence: string, index: number) => (
              <div key={index} className="p-6 border border-slate-200 rounded-lg bg-white text-center">
                <p className="text-slate-700 font-medium">{consequence}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Conclusion */}
        <div className="text-center">
          <p className="text-xl text-slate-900 font-semibold">{t('painPoints.conclusion')}</p>
        </div>
      </div>
    </section>
  );
};

export default PainPointsSection;
