import React from 'react';
import { Link, LinkProps } from 'react-router-dom';
import { useLanguageRouting } from '../utils/routing';

interface LocalizedLinkProps extends Omit<LinkProps, 'to'> {
  to: string;
  children: React.ReactNode;
}

// Language-aware Link component
const LocalizedLink: React.FC<LocalizedLinkProps> = ({ to, children, ...props }) => {
  const { getLocalizedPath } = useLanguageRouting();
  const localizedTo = getLocalizedPath(to);

  return (
    <Link to={localizedTo} {...props}>
      {children}
    </Link>
  );
};

export default LocalizedLink;
