import React from 'react';
import { useTranslation } from 'react-i18next';

const ProcessSection: React.FC = () => {
  const { t } = useTranslation();
  const steps = t('process.steps', { returnObjects: true }) as Array<{
    number: string;
    title: string;
    description: string;
    items?: string[];
  }>;

  return (
    <section className="py-24 bg-slate-50">
      <div className="max-w-5xl mx-auto px-6">
        <div className="text-center mb-20">
          <h2 className="text-4xl md:text-5xl font-semibold text-slate-900 mb-6 leading-tight">
            {t('process.title')}
          </h2>
        </div>

        <div className="space-y-8">
          {steps.map((step, index) => (
            <div key={index} className="p-8 border border-slate-200 rounded-lg">
              <div className="flex items-start space-x-6">
                <div className="flex-shrink-0">
                  <div className="w-12 h-12 bg-slate-900 text-white rounded-lg flex items-center justify-center text-lg font-semibold">
                    {step.number}
                  </div>
                </div>
                <div className="flex-1">
                  <h3 className="text-2xl font-semibold text-slate-900 mb-3">{step.title}</h3>
                  <p className="text-slate-600 leading-relaxed mb-4">{step.description}</p>
                  {step.items && (
                    <ul className="space-y-2 mt-4">
                      {step.items.map((item: string, itemIndex: number) => (
                        <li key={itemIndex} className="text-slate-600">
                          {item}
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="mt-16 text-center">
          <p className="text-lg text-slate-600">{t('process.noDevelopers')}</p>
        </div>
      </div>
    </section>
  );
};

export default ProcessSection;
