import React from 'react';
import { Helmet } from 'react-helmet-async';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import i18n from '../i18n';
import HeroSection from '../components/sections/HeroSection';
import ResponsibilitySection from '../components/sections/ResponsibilitySection';
import PainPointsSection from '../components/sections/PainPointsSection';
import ServicesSection from '../components/sections/ServicesSection';
import ProcessSection from '../components/sections/ProcessSection';
import PricingSection from '../components/sections/PricingSection';
import CaseStudiesSection from '../components/sections/CaseStudiesSection';
import ComparisonSection from '../components/sections/ComparisonSection';
import FAQSection from '../components/sections/FAQSection';
import MetricsSection from '../components/sections/MetricsSection';
import AboutSection from '../components/sections/AboutSection';
import ContactSection from '../components/sections/ContactSection';

const HomeGerman: React.FC = () => {
  const { t } = useTranslation();

  // Force German language
  React.useEffect(() => {
    i18n.changeLanguage('de');
  }, []);

  // Handle hash navigation - bulletproof version
  React.useEffect(() => {
    const scrollToHash = () => {
      const hash = window.location.hash;
      if (!hash) return;

      const elementId = hash.substring(1);
      const element = document.getElementById(elementId);

      if (element) {
        element.scrollIntoView({ behavior: 'smooth', block: 'start' });
      } else {
        // If element not found, try again in 100ms
        setTimeout(scrollToHash, 100);
      }
    };

    // Wait for page to fully load, then scroll
    if (document.readyState === 'complete') {
      setTimeout(scrollToHash, 500);
    } else {
      window.addEventListener('load', () => {
        setTimeout(scrollToHash, 500);
      });
    }

    // Also listen for hash changes
    window.addEventListener('hashchange', scrollToHash);

    return () => {
      window.removeEventListener('hashchange', scrollToHash);
    };
  }, []);

  return (
    <div>
      <Helmet>
        <title>Fynteq - Zahlungsabwicklung Integration Services</title>
        <meta name="description" content="Professionelle Zahlungsabwicklung für SaaS, E-Commerce & Startups. Experte für Stripe & PayPal Setup in 3-7 Tagen. Abonnement-Abrechnung, Marktplatz-Zahlungen, PCI-konform. Erfahrung mit Hunderten von Händler-Integrationen. Kostenlose Beratung verfügbar." />
        <link rel="canonical" href="https://fynteq.com/de" />
        <link rel="alternate" hrefLang="en" href="https://fynteq.com/" />
        <link rel="alternate" hrefLang="de" href="https://fynteq.com/de" />
        <link rel="alternate" hrefLang="x-default" href="https://fynteq.com/" />
        <script type="application/ld+json">
          {JSON.stringify({
            "@context": "https://schema.org",
            "@type": "WebPage",
            "name": "Fynteq - Zahlungsabwicklung Integration Services",
            "description": "Professionelle Zahlungsabwicklung für SaaS, E-Commerce & Startups. Experte für Stripe & PayPal Setup in 3-7 Tagen. Abonnement-Abrechnung, Marktplatz-Zahlungen, PCI-konform. Erfahrung mit Hunderten von Händler-Integrationen. Kostenlose Beratung verfügbar.",
            "url": "https://fynteq.com/de",
            "mainEntity": {
              "@type": "Organization",
              "name": "Fynteq",
              "url": "https://fynteq.com",
              "description": "Professionelle Zahlungsabwicklung für SaaS, E-Commerce & Startups. Experte für Stripe & PayPal Setup in 3-7 Tagen. Abonnement-Abrechnung, Marktplatz-Zahlungen, PCI-konform. Erfahrung mit Hunderten von Händler-Integrationen. Kostenlose Beratung verfügbar."
            }
          })}
        </script>
      </Helmet>
      <HeroSection />
      <ResponsibilitySection />
      <PainPointsSection />
      <ServicesSection />
      <ProcessSection />
      <PricingSection />
      <CaseStudiesSection />

      {/* Blog Preview Section */}
      <section className="section-spacing bg-slate-50">
        <div className="max-w-7xl mx-auto px-6">
          <div className="text-center mb-16">
            <h2 className="heading-lg text-slate-900 mb-6">
              {t('home.blog.title')}
            </h2>
            <p className="text-lg text-slate-600 max-w-3xl mx-auto">
              {t('home.blog.subtitle')}
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
            {[1, 2, 3].map((i) => (
              <div key={i} className="bg-white rounded-xl shadow-sm border border-slate-200 overflow-hidden hover:shadow-md transition-shadow">
                <div className="p-6">
                  <div className="flex items-center mb-4">
                    <span className="text-sm font-medium text-blue-600 bg-blue-50 px-3 py-1 rounded-full">
                      {t(`home.blog.card${i}.category`)}
                    </span>
                  </div>
                  <h3 className="text-xl font-semibold text-slate-900 mb-3">
                    {t(`home.blog.card${i}.title`)}
                  </h3>
                  <p className="text-slate-600 mb-4">
                    {t(`home.blog.card${i}.excerpt`)}
                  </p>
                  <Link 
                    to={`/de/blog/${t(`home.blog.card${i}.slug`)}`}
                    className="text-blue-600 hover:text-blue-700 font-medium inline-flex items-center"
                  >
                    {t('home.blog.readMore')}
                    <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                    </svg>
                  </Link>
                </div>
              </div>
            ))}
          </div>

          <div className="text-center">
            <Link 
              to="/de/blog"
              className="button-secondary"
            >
              {t('home.blog.viewAll')}
            </Link>
          </div>
        </div>
      </section>

      <ComparisonSection />
      <FAQSection />
      <MetricsSection />
      <AboutSection />
      <ContactSection />
    </div>
  );
};

export default HomeGerman;
