import React from 'react';
import { useTranslation } from 'react-i18next';
import { trackButtonClick, trackConsultationClick } from '../../utils/analytics';

const HeroSection: React.FC = () => {
  const { t } = useTranslation();

  return (
    <section className="py-32 bg-white">
      <div className="max-w-5xl mx-auto px-6">
        <div className="text-center max-w-3xl mx-auto">
          <h1 className="text-6xl md:text-7xl font-semibold text-slate-900 mb-8 leading-tight tracking-tight">
            {t('hero.title')}
          </h1>
          <p className="text-xl text-slate-600 leading-relaxed mb-6 max-w-2xl mx-auto">
            {t('hero.subtitle')}
          </p>
          <p className="text-lg text-slate-500 mb-12 max-w-2xl mx-auto">
            {t('hero.subtitle2')}
          </p>

          <div className="flex flex-col sm:flex-row gap-3 justify-center mb-16">
            <a 
              href="#pricing" 
              className="px-6 py-3 bg-slate-900 text-white rounded-lg font-medium hover:bg-slate-800 transition-colors"
              onClick={() => trackButtonClick('Start Free Trial', 'Hero Section')}
            >
              {t('hero.startFreeTrial')}
            </a>
            <a 
              href="https://calendly.com/fynteq/30min" 
              target="_blank" 
              rel="noopener noreferrer" 
              className="px-6 py-3 bg-white text-slate-900 border border-slate-200 rounded-lg font-medium hover:bg-slate-50 transition-colors"
              onClick={() => trackConsultationClick('Hero CTA')}
            >
              {t('hero.bookCall')}
            </a>
          </div>

          <div className="flex flex-wrap justify-center items-center gap-4 text-sm text-slate-500">
            <span>Stripe</span>
            <span>·</span>
            <span>PayPal</span>
            <span>·</span>
            <span>Shopify</span>
            <span>·</span>
            <span>Apple Pay</span>
            <span>·</span>
            <span>Google Pay</span>
            <span>·</span>
            <span>Adyen</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
