import React from 'react';
import { useTranslation } from 'react-i18next';

const ProcessSection: React.FC = () => {
  const { t } = useTranslation();
  const steps = t('process.steps', { returnObjects: true }) as Array<{
    number: string;
    title: string;
    description: string;
    items?: string[];
  }>;

  return (
    <section className="py-24 bg-white">
      <div className="max-w-6xl mx-auto px-6">
        <div className="text-center mb-20">
          <h2 className="text-4xl md:text-5xl font-semibold text-slate-900 mb-6 leading-tight">
            {t('process.title')}
          </h2>
        </div>

        {/* Workflow Steps */}
        <div className="relative">
          {/* Connection lines - desktop only */}
          <div className="hidden lg:block absolute top-16 left-0 right-0 h-0.5 bg-slate-200" style={{ marginLeft: 'calc(12.5% - 1rem)', marginRight: 'calc(12.5% - 1rem)' }}></div>

          <div className="grid grid-cols-1 lg:grid-cols-4 gap-8 lg:gap-4">
            {steps.map((step, index) => (
              <div key={index} className="relative">
                {/* Step Card */}
                <div className="bg-white border border-slate-200 rounded-lg p-6 hover:border-slate-300 transition-colors h-full">
                  {/* Step Number */}
                  <div className="flex items-center mb-4">
                    <div className="w-8 h-8 bg-slate-900 text-white rounded-full flex items-center justify-center text-sm font-semibold flex-shrink-0">
                      {step.number}
                    </div>
                    {index < steps.length - 1 && (
                      <div className="hidden lg:block absolute top-8 left-full w-4 h-0.5 bg-slate-200" style={{ marginLeft: '0.5rem' }}>
                        <div className="absolute right-0 top-1/2 -translate-y-1/2 w-2 h-2 bg-slate-200 rounded-full"></div>
                      </div>
                    )}
                  </div>

                  {/* Step Content */}
                  <div>
                    <h3 className="text-lg font-semibold text-slate-900 mb-3 leading-tight">
                      {step.title}
                    </h3>
                    <p className="text-sm text-slate-600 leading-relaxed mb-4">
                      {step.description}
                    </p>
                    {step.items && (
                      <ul className="space-y-1.5 mt-4">
                        {step.items.map((item: string, itemIndex: number) => (
                          <li key={itemIndex} className="text-xs text-slate-500 flex items-start">
                            <span className="w-1 h-1 rounded-full bg-slate-400 mt-1.5 mr-2 flex-shrink-0"></span>
                            <span>{item}</span>
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="mt-16 text-center">
          <p className="text-base text-slate-600">{t('process.noDevelopers')}</p>
        </div>
      </div>
    </section>
  );
};

export default ProcessSection;
