import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard: React.FC = () => {
  const navigate = useNavigate();
  const [paymentData, setPaymentData] = useState<any>(null);
  const [connectedProviders, setConnectedProviders] = useState<string[]>([]);

  useEffect(() => {
    // Get payment data from sessionStorage
    const stored = sessionStorage.getItem('paymentData');
    if (stored) {
      setPaymentData(JSON.parse(stored));
    } else {
      // If no payment data, redirect to home
      navigate('/');
    }
  }, [navigate]);

  const providers = [
    { id: 'stripe', name: 'Stripe', logo: '/images/brands/stripe-logo-AQEyPRPODaTM3Ern.png.avif' },
    { id: 'paypal', name: 'PayPal', logo: '/images/brands/pngimg.com---paypal_png7-mePvDEDJbQCke023.png.avif' },
    { id: 'shopify', name: 'Shopify', logo: null }
  ];

  const handleConnectProvider = async (providerId: string) => {
    // Here you would integrate with OAuth for Stripe/PayPal
    // For now, we'll simulate the connection

    // Simulate OAuth flow
    const confirmed = window.confirm(`Connect your ${providers.find(p => p.id === providerId)?.name} account?`);

    if (confirmed) {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      setConnectedProviders([...connectedProviders, providerId]);

      alert(`${providers.find(p => p.id === providerId)?.name} account connected successfully!`);
    }
  };

  if (!paymentData) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <div className="text-center">
          <p className="text-slate-600">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Header */}
      <div className="bg-white border-b border-slate-200">
        <div className="max-w-6xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-semibold text-slate-900">Welcome to Fynteq Pulse</h1>
              <p className="text-slate-600 text-sm mt-1">Let's connect your payment providers to get started</p>
            </div>
            <div className="text-right">
              <div className="text-sm text-slate-600">Plan</div>
              <div className="font-semibold text-slate-900">{paymentData.package?.charAt(0).toUpperCase() + paymentData.package?.slice(1)}</div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-6 py-12">
        {/* Success Message */}
        <div className="mb-8 p-6 bg-green-50 border border-green-200 rounded-2xl">
          <div className="flex items-start gap-4">
            <div className="w-10 h-10 bg-green-500 rounded-full flex items-center justify-center flex-shrink-0">
              <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
              </svg>
            </div>
            <div className="flex-1">
              <h2 className="text-lg font-semibold text-green-900 mb-1">Account Created Successfully!</h2>
              <p className="text-sm text-green-700">
                Your {paymentData.package?.charAt(0).toUpperCase() + paymentData.package?.slice(1)} plan is active. 
                Connect your payment providers to start monitoring.
              </p>
            </div>
          </div>
        </div>

        {/* Connect Providers Section */}
        <div className="mb-8">
          <h2 className="text-xl font-semibold text-slate-900 mb-6">Connect Payment Providers</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {providers.map((provider) => {
              const isConnected = connectedProviders.includes(provider.id);

              return (
                <div
                  key={provider.id}
                  className={`p-6 border rounded-2xl transition-all ${
                    isConnected
                      ? 'bg-green-50 border-green-200'
                      : 'bg-white border-slate-100 hover:border-slate-200'
                  }`}
                >
                  <div className="flex items-center justify-between mb-4">
                    <div className="flex items-center gap-3">
                      {provider.logo ? (
                        <img
                          src={provider.logo}
                          alt={provider.name}
                          className="h-8 w-auto opacity-60 grayscale"
                        />
                      ) : (
                        <div className="h-8 w-8 bg-slate-200 rounded flex items-center justify-center">
                          <span className="text-xs font-medium text-slate-600">{provider.name.charAt(0)}</span>
                        </div>
                      )}
                      <div>
                        <h3 className="font-semibold text-slate-900">{provider.name}</h3>
                        {isConnected && (
                          <span className="text-xs text-green-600 font-medium">Connected</span>
                        )}
                      </div>
                    </div>
                    {isConnected && (
                      <div className="w-6 h-6 bg-green-500 rounded-full flex items-center justify-center">
                        <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                        </svg>
                      </div>
                    )}
                  </div>

                  {!isConnected ? (
                    <button
                      onClick={() => handleConnectProvider(provider.id)}
                      className="w-full py-2.5 px-4 bg-slate-900 text-white rounded-xl font-medium hover:bg-slate-800 transition-colors text-sm"
                    >
                      Connect {provider.name}
                    </button>
                  ) : (
                    <div className="text-sm text-slate-600">
                      <p className="mb-2">âœ“ Monitoring active</p>
                      <button
                        onClick={() => setConnectedProviders(connectedProviders.filter(p => p !== provider.id))}
                        className="text-slate-400 hover:text-slate-600 text-xs"
                      >
                        Disconnect
                      </button>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>

        {/* Next Steps */}
        <div className="bg-white border border-slate-100 rounded-2xl p-6">
          <h2 className="text-lg font-semibold text-slate-900 mb-4">Next Steps</h2>
          <ul className="space-y-3">
            <li className="flex items-start gap-3">
              <div className={`w-6 h-6 rounded-full flex items-center justify-center flex-shrink-0 ${
                connectedProviders.length > 0 ? 'bg-green-500' : 'bg-slate-200'
              }`}>
                {connectedProviders.length > 0 ? (
                  <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                  </svg>
                ) : (
                  <span className="text-xs text-slate-600 font-medium">1</span>
                )}
              </div>
              <div>
                <p className="font-medium text-slate-900">Connect at least one payment provider</p>
                <p className="text-sm text-slate-600">Stripe, PayPal, or Shopify</p>
              </div>
            </li>
            <li className="flex items-start gap-3">
              <div className="w-6 h-6 rounded-full bg-slate-200 flex items-center justify-center flex-shrink-0">
                <span className="text-xs text-slate-600 font-medium">2</span>
              </div>
              <div>
                <p className="font-medium text-slate-900">Configure monitoring settings</p>
                <p className="text-sm text-slate-600">Set up alerts and notifications</p>
              </div>
            </li>
            <li className="flex items-start gap-3">
              <div className="w-6 h-6 rounded-full bg-slate-200 flex items-center justify-center flex-shrink-0">
                <span className="text-xs text-slate-600 font-medium">3</span>
              </div>
              <div>
                <p className="font-medium text-slate-900">Start monitoring your payments</p>
                <p className="text-sm text-slate-600">Get real-time alerts and insights</p>
              </div>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
