import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

const Signup: React.FC = () => {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const packageName = searchParams.get('package') || 'starter';
  const billing = searchParams.get('billing') || 'monthly';

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    company: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  const packageInfo: Record<string, { name: string; price: string }> = {
    'starter': { name: 'Starter', price: '$29' },
    'pro': { name: 'Pro', price: '$79' },
    'scale': { name: 'Scale', price: '$149' }
  };

  const features = [
    { name: 'Payment Health Score', starter: true, pro: true, scale: true },
    { name: 'Failed Payment Alerts', starter: true, pro: true, scale: true },
    { name: 'Checkout Monitoring', starter: 'Basic', pro: 'Advanced', scale: 'Advanced' },
    { name: 'Subscription Health', starter: false, pro: true, scale: true },
    { name: 'Renewal Predictions', starter: false, pro: true, scale: true },
    { name: 'Chargeback Monitoring', starter: false, pro: true, scale: true },
    { name: 'AI Fix Suggestions', starter: 'Limited', pro: 'Full', scale: 'Full' },
    { name: 'Multi-Account Monitoring', starter: false, pro: false, scale: true },
    { name: 'Vendor/KYC Monitoring', starter: false, pro: false, scale: true },
    { name: 'Slack Alerts', starter: false, pro: false, scale: true },
    { name: 'Daily Digest', starter: false, pro: false, scale: true },
    { name: 'CRM Sync Alerts', starter: false, pro: false, scale: true },
  ];

  const currentPackage = packageInfo[packageName] || packageInfo['starter'];
  const monthlyPrice = parseInt(currentPackage.price.replace('$', ''));
  const finalPrice = billing === 'yearly' ? Math.round(monthlyPrice * 12 * 0.66) : monthlyPrice;

  // Get features for the selected package
  const getPackageFeatures = () => {
    return features
      .map(feature => {
        const value = feature[packageName as keyof typeof feature];
        if (value === true || (typeof value === 'string' && value !== 'false')) {
          return {
            name: feature.name,
            value: value === true ? true : value
          };
        }
        return null;
      })
      .filter(f => f !== null);
  };

  const packageFeatures = getPackageFeatures();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    try {
      // Here you would typically call your signup API
      // For now, we'll simulate and redirect to payment
      const userData = {
        ...formData,
        package: packageName,
        billing: billing,
        price: finalPrice
      };

      // Store in sessionStorage for payment page
      sessionStorage.setItem('signupData', JSON.stringify(userData));

      // Redirect to payment page
      navigate(`/payment?package=${packageName}&billing=${billing}`);
    } catch (err) {
      setError('Signup failed. Please try again.');
      console.error('Signup error:', err);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="min-h-screen bg-white py-12">
      <div className="max-w-md mx-auto px-6">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-semibold text-slate-900 mb-2">Create Your Account</h1>
          <p className="text-slate-600">Sign up for {currentPackage.name} Plan</p>
        </div>

        <div className="bg-white border border-slate-100 rounded-2xl p-8 shadow-sm">
          {/* Package Summary */}
          <div className="mb-6 p-4 bg-slate-50 rounded-xl border border-slate-100">
            <div className="flex justify-between items-center mb-2">
              <span className="text-sm font-medium text-slate-600">Plan</span>
              <span className="text-sm font-semibold text-slate-900">{currentPackage.name}</span>
            </div>
            <div className="flex justify-between items-center mb-2">
              <span className="text-sm font-medium text-slate-600">Billing</span>
              <span className="text-sm font-semibold text-slate-900 capitalize">{billing}</span>
            </div>
            <div className="flex justify-between items-center pt-2 border-t border-slate-200">
              <span className="text-base font-semibold text-slate-900">Total</span>
              <span className="text-xl font-bold text-slate-900">
                ${finalPrice.toLocaleString()}/{billing === 'yearly' ? 'yr' : 'mo'}
              </span>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-slate-700 mb-2">
                Full Name *
              </label>
              <input
                type="text"
                id="name"
                name="name"
                required
                value={formData.name}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-slate-200 rounded-xl focus:ring-2 focus:ring-slate-900 focus:border-transparent transition-all"
                placeholder="John Doe"
              />
            </div>

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-slate-700 mb-2">
                Email Address *
              </label>
              <input
                type="email"
                id="email"
                name="email"
                required
                value={formData.email}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-slate-200 rounded-xl focus:ring-2 focus:ring-slate-900 focus:border-transparent transition-all"
                placeholder="you@example.com"
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-slate-700 mb-2">
                Password *
              </label>
              <input
                type="password"
                id="password"
                name="password"
                required
                minLength={8}
                value={formData.password}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-slate-200 rounded-xl focus:ring-2 focus:ring-slate-900 focus:border-transparent transition-all"
                placeholder="Minimum 8 characters"
              />
            </div>

            <div>
              <label htmlFor="company" className="block text-sm font-medium text-slate-700 mb-2">
                Company Name
              </label>
              <input
                type="text"
                id="company"
                name="company"
                value={formData.company}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-slate-200 rounded-xl focus:ring-2 focus:ring-slate-900 focus:border-transparent transition-all"
                placeholder="Your Company"
              />
            </div>

            {error && (
              <div className="p-3 bg-red-50 border border-red-200 rounded-xl">
                <p className="text-sm text-red-700">{error}</p>
              </div>
            )}

            <button
              type="submit"
              disabled={isSubmitting}
              className={`w-full py-3 px-6 rounded-xl font-medium text-center transition-all ${
                isSubmitting
                  ? 'bg-slate-400 cursor-not-allowed text-white'
                  : 'button-primary'
              }`}
            >
              {isSubmitting ? 'Creating Account...' : 'Create Account & Continue to Payment'}
            </button>
          </form>

          <p className="mt-6 text-xs text-center text-slate-500">
            By signing up, you agree to our{' '}
            <a href="/terms" className="text-slate-900 hover:underline">Terms of Service</a>
            {' '}and{' '}
            <a href="/privacy" className="text-slate-900 hover:underline">Privacy Policy</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Signup;
