import React from 'react';
import { useTranslation } from 'react-i18next';
import { trackPricingView } from '../../utils/analytics';

const PricingSection: React.FC = () => {
  const { t } = useTranslation();

  return (
    <section id="pricing" className="py-24 bg-white">
      <div className="max-w-5xl mx-auto px-6">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-semibold text-slate-900 mb-4 leading-tight">
            {t('pricing.title')}
          </h2>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {t('pricing.plans', { returnObjects: true }).map((plan: any, index: number) => (
            <div key={index} className={`p-8 border rounded-lg ${plan.popular ? 'border-slate-900 border-2' : 'border-slate-200 hover:border-slate-300'} transition-colors`}>
              {plan.popular && (
                <div className="mb-4">
                  <span className="text-xs font-medium text-slate-900 bg-slate-100 px-2 py-1 rounded">
                    {t('pricing.mostPopular')}
                  </span>
                </div>
              )}

              <div className="mb-8">
                <h3 className="text-xl font-semibold text-slate-900 mb-4">{plan.name}</h3>
                <div className="mb-4">
                  <span className="text-4xl font-semibold text-slate-900">{plan.price}</span>
                  <span className="text-slate-600">/mo</span>
                </div>
                <p className="text-slate-600 mb-6">{plan.description}</p>
                <div className="p-4 bg-slate-50 rounded-lg border border-slate-200">
                  <p className="text-sm font-medium text-slate-900">{plan.for}</p>
                </div>
              </div>

              <ul className="space-y-3 mb-8">
                {plan.features.map((feature: string, featureIndex: number) => (
                  <li key={featureIndex} className="text-slate-600 text-sm">
                    {feature}
                  </li>
                ))}
              </ul>

              <a 
                href={plan.buttonLink || "#pricing"} 
                target={plan.buttonLink ? "_blank" : undefined}
                rel={plan.buttonLink ? "noopener noreferrer" : undefined}
                className={`w-full py-3 px-6 rounded-lg font-medium text-center block ${
                  plan.popular 
                    ? 'button-primary' 
                    : 'button-secondary'
                }`}
                onClick={() => trackPricingView(plan.name)}
              >
                {plan.button}
              </a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default PricingSection;
