import React, { createContext, useContext, useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import i18n from '../i18n';
import { detectUserLanguage, saveLanguagePreference, getLanguageFromPath, shouldRedirectToGerman } from '../utils/languageDetection';

interface LanguageContextType {
  language: string;
  setLanguage: (lang: string) => void;
  isGerman: boolean;
}

const LanguageContext = createContext<LanguageContextType | undefined>(undefined);

export const useLanguage = () => {
  const context = useContext(LanguageContext);
  if (context === undefined) {
    throw new Error('useLanguage must be used within a LanguageProvider');
  }
  return context;
};

interface LanguageProviderProps {
  children: React.ReactNode;
}

export const LanguageProvider: React.FC<LanguageProviderProps> = ({ children }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const [language, setLanguageState] = useState<string>('en');
  const [hasCheckedAutoRedirect, setHasCheckedAutoRedirect] = useState(false);

  // Set language and save preference
  const setLanguage = (newLanguage: string) => {
    setLanguageState(newLanguage);
    saveLanguagePreference(newLanguage as 'en' | 'de');
    i18n.changeLanguage(newLanguage);
  };

  // Auto-redirect to German if browser language is German (first visit only)
  useEffect(() => {
    if (!hasCheckedAutoRedirect && location.pathname === '/') {
      if (shouldRedirectToGerman(location.pathname)) {
        console.log('Auto-detecting German browser, redirecting to /de');
        navigate('/de', { replace: true });
      }
      setHasCheckedAutoRedirect(true);
    }
  }, [hasCheckedAutoRedirect, location.pathname, navigate]);

  // Initialize and sync language from URL
  useEffect(() => {
    const urlLanguage = getLanguageFromPath(location.pathname);

    if (urlLanguage) {
      // URL has explicit language, use it and save preference
      if (urlLanguage !== language) {
        setLanguageState(urlLanguage);
        i18n.changeLanguage(urlLanguage);
        // Save this as user preference (they navigated to this language)
        saveLanguagePreference(urlLanguage);
      }
    } else {
      // No explicit language in URL, detect from preferences
      const detectedLanguage = detectUserLanguage();
      if (detectedLanguage !== language) {
        setLanguageState(detectedLanguage);
        i18n.changeLanguage(detectedLanguage);
      }
    }
  }, [location.pathname]);

  const isGerman = language === 'de';

  return (
    <LanguageContext.Provider value={{ language, setLanguage, isGerman }}>
      {children}
    </LanguageContext.Provider>
  );
};
