import React, { createContext, useContext, useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import i18n from '../i18n';

interface LanguageContextType {
  language: string;
  setLanguage: (lang: string) => void;
  isGerman: boolean;
}

const LanguageContext = createContext<LanguageContextType | undefined>(undefined);

export const useLanguage = () => {
  const context = useContext(LanguageContext);
  if (context === undefined) {
    throw new Error('useLanguage must be used within a LanguageProvider');
  }
  return context;
};

interface LanguageProviderProps {
  children: React.ReactNode;
}

export const LanguageProvider: React.FC<LanguageProviderProps> = ({ children }) => {
  const location = useLocation();
  const [language, setLanguageState] = useState<string>('en');

  // Set language
  const setLanguage = (newLanguage: string) => {
    setLanguageState(newLanguage);
    // Save to cookie
    document.cookie = `language=${newLanguage}; path=/; max-age=31536000`;
    // Change i18n language
    const { i18n } = require('react-i18next');
    i18n.changeLanguage(newLanguage);
  };

  // Initialize language on mount and URL change
  useEffect(() => {
    // Get language from URL
    const getLanguageFromURL = () => {
      return location.pathname.startsWith('/de') ? 'de' : 'en';
    };

    // Get language from cookie
    const getLanguageFromCookie = () => {
      const cookies = document.cookie.split(';');
      const languageCookie = cookies.find(cookie => cookie.trim().startsWith('language='));
      if (languageCookie) {
        return languageCookie.split('=')[1];
      }
      return null;
    };

    const urlLanguage = getLanguageFromURL();
    const cookieLanguage = getLanguageFromCookie();

    // Priority: URL > Cookie > Default (en)
    const targetLanguage = urlLanguage || cookieLanguage || 'en';

    if (targetLanguage !== language) {
      setLanguageState(targetLanguage);
      const { i18n } = require('react-i18next');
      i18n.changeLanguage(targetLanguage);
    }
  }, [location.pathname, language]);

  const isGerman = language === 'de';

  return (
    <LanguageContext.Provider value={{ language, setLanguage, isGerman }}>
      {children}
    </LanguageContext.Provider>
  );
};
