import React from 'react';
import { useTranslation } from 'react-i18next';

const PainPointsSection: React.FC = () => {
  const { t } = useTranslation();

  const statistics = t('painPoints.statistics', { returnObjects: true }) as Array<{stat: string, description: string}>;
  const consequences = t('painPoints.ifNotMonitoring.consequences', { returnObjects: true }) as string[];

  return (
    <section className="py-32 bg-white">
      <div className="max-w-4xl mx-auto px-6">
        <div className="mb-24">
          <h2 className="text-5xl md:text-6xl font-semibold text-slate-900 mb-4 leading-tight">
            {t('painPoints.title')}
          </h2>
          <p className="text-lg text-slate-500">
            {t('painPoints.subtitle')}
          </p>
        </div>

        {/* Statistics - Ultra Minimal */}
        <div className="space-y-8 mb-24">
          {statistics.map((stat: any, index: number) => (
            <div key={index}>
              <div className="text-4xl font-semibold text-slate-900 mb-2">{stat.stat}</div>
              <p className="text-base text-slate-600">{stat.description}</p>
            </div>
          ))}
        </div>

        {/* If Not Monitoring */}
        <div className="mb-24">
          <h3 className="text-2xl font-semibold text-slate-900 mb-8">
            {t('painPoints.ifNotMonitoring.title')}
          </h3>
          <div className="space-y-4">
            {consequences.map((consequence: string, index: number) => (
              <p key={index} className="text-lg text-slate-600">{consequence}</p>
            ))}
          </div>
        </div>

        {/* Conclusion */}
        <div>
          <p className="text-xl text-slate-900 font-medium">{t('painPoints.conclusion')}</p>
        </div>
      </div>
    </section>
  );
};

export default PainPointsSection;

export default PainPointsSection;
