import React from 'react';
import { Helmet } from 'react-helmet-async';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import HeroSection from '../components/sections/HeroSection';
import ResponsibilitySection from '../components/sections/ResponsibilitySection';
import PainPointsSection from '../components/sections/PainPointsSection';
import ServicesSection from '../components/sections/ServicesSection';
import ProcessSection from '../components/sections/ProcessSection';
import PricingSection from '../components/sections/PricingSection';
import CaseStudiesSection from '../components/sections/CaseStudiesSection';
import ComparisonSection from '../components/sections/ComparisonSection';
import FAQSection from '../components/sections/FAQSection';
import MetricsSection from '../components/sections/MetricsSection';
import AboutSection from '../components/sections/AboutSection';
import ContactSection from '../components/sections/ContactSection';
const Home: React.FC = () => {
  const { t, i18n } = useTranslation();
  const [isReady, setIsReady] = React.useState(false);

  // Force language change based on URL
  React.useEffect(() => {
    const isGerman = window.location.pathname.startsWith('/de');
    const targetLanguage = isGerman ? 'de' : 'en';

    if (i18n.language !== targetLanguage) {
      i18n.changeLanguage(targetLanguage).then(() => {
        setIsReady(true);
      });
    } else {
      setIsReady(true);
    }
  }, [i18n]);

  if (!isReady) {
    return <div>Loading...</div>;
  }

  // Handle hash navigation
  React.useEffect(() => {
    const handleHashNavigation = () => {
      const hash = window.location.hash;
      if (hash) {
        const elementId = hash.substring(1); // Remove the # symbol
        const element = document.getElementById(elementId);
        if (element) {
          // Wait a bit for the page to fully load
          setTimeout(() => {
            element.scrollIntoView({ behavior: 'smooth' });
          }, 500);
        }
      }
    };

    // Handle hash on page load
    handleHashNavigation();

    // Listen for hash changes
    window.addEventListener('hashchange', handleHashNavigation);

    return () => {
      window.removeEventListener('hashchange', handleHashNavigation);
    };
  }, []);

  return (
    <div>
      <Helmet>
        <title>{t('home.meta.title')}</title>
        <meta name="description" content={t('home.meta.description')} />
        <link rel="canonical" href="https://www.fynteq.com" />
        <script type="application/ld+json">
          {JSON.stringify({
            "@context": "https://schema.org",
            "@type": "WebPage",
            "name": t('home.meta.title'),
            "description": t('home.meta.description'),
            "url": "https://www.fynteq.com",
            "mainEntity": {
              "@type": "Organization",
              "name": "Fynteq",
              "url": "https://www.fynteq.com",
              "description": t('home.meta.description')
            }
          })}
        </script>
      </Helmet>
      <HeroSection />
      <ResponsibilitySection />
      <PainPointsSection />
      <ServicesSection />
      <ProcessSection />
      <PricingSection />
      <CaseStudiesSection />

      {/* Blog Preview Section */}
      <section className="section-spacing bg-white">
        <div className="max-w-7xl mx-auto px-6">
          <div className="text-center mb-16">
            <h2 className="heading-lg text-slate-900 mb-6 text-balance">
              {t('blog.preview.title')}
            </h2>
            <p className="text-lg text-slate-600 max-w-2xl mx-auto text-balance">
              {t('blog.preview.subtitle')}
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-12">
            <div className="card hover-lift">
              <div className="aspect-video rounded-t-lg overflow-hidden">
                <img 
                  src="https://images.unsplash.com/photo-1556742049-0cfed4f6a45d?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80" 
                  alt="Stripe integration guide"
                  className="w-full h-full object-cover"
                  loading="lazy"
                />
              </div>
              <div className="p-6">
                <span className="bg-blue-100 text-blue-800 text-xs font-semibold px-2 py-1 rounded-full mb-3 inline-block">
                  {t('home.blog.card1.category')}
                </span>
                <h3 className="text-lg font-semibold text-slate-900 mb-2">
                  <Link to="/blog/stripe-integration-complete-guide" className="hover:text-blue-600 transition-colors">
                    {t('home.blog.card1.title')}
                  </Link>
                </h3>
                <p className="text-slate-600 text-sm mb-4">
                  {t('home.blog.card1.excerpt')}
                </p>
                <Link to="/blog/stripe-integration-complete-guide" className="text-blue-600 hover:text-blue-700 font-medium text-sm">
                  {t('home.blog.readMore')} →
                </Link>
              </div>
            </div>

            <div className="card hover-lift">
              <div className="aspect-video rounded-t-lg overflow-hidden">
                <img 
                  src="https://images.unsplash.com/photo-1551288049-bebda4e38f71?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80" 
                  alt="SaaS billing best practices"
                  className="w-full h-full object-cover"
                  loading="lazy"
                />
              </div>
              <div className="p-6">
                <span className="bg-green-100 text-green-800 text-xs font-semibold px-2 py-1 rounded-full mb-3 inline-block">
                  {t('home.blog.card2.category')}
                </span>
                <h3 className="text-lg font-semibold text-slate-900 mb-2">
                  <Link to="/blog/saas-subscription-billing-best-practices" className="hover:text-blue-600 transition-colors">
                    {t('home.blog.card2.title')}
                  </Link>
                </h3>
                <p className="text-slate-600 text-sm mb-4">
                  {t('home.blog.card2.excerpt')}
                </p>
                <Link to="/blog/saas-subscription-billing-best-practices" className="text-blue-600 hover:text-blue-700 font-medium text-sm">
                  {t('home.blog.readMore')} →
                </Link>
              </div>
            </div>

            <div className="card hover-lift">
              <div className="aspect-video rounded-t-lg overflow-hidden">
                <img 
                  src="https://images.unsplash.com/photo-1460925895917-afdab827c52f?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80" 
                  alt="Payment security guide"
                  className="w-full h-full object-cover"
                  loading="lazy"
                />
              </div>
              <div className="p-6">
                <span className="bg-purple-100 text-purple-800 text-xs font-semibold px-2 py-1 rounded-full mb-3 inline-block">
                  {t('home.blog.card3.category')}
                </span>
                <h3 className="text-lg font-semibold text-slate-900 mb-2">
                  <Link to="/blog/pci-compliance-checklist-online-businesses" className="hover:text-blue-600 transition-colors">
                    {t('home.blog.card3.title')}
                  </Link>
                </h3>
                <p className="text-slate-600 text-sm mb-4">
                  {t('home.blog.card3.excerpt')}
                </p>
                <Link to="/blog/pci-compliance-checklist-online-businesses" className="text-blue-600 hover:text-blue-700 font-medium text-sm">
                  {t('home.blog.readMore')} →
                </Link>
              </div>
            </div>
          </div>

          <div className="text-center">
            <Link to="/blog" className="button-secondary">
              {t('home.blog.viewAll')}
            </Link>
          </div>
        </div>
      </section>

      <ComparisonSection />
      <FAQSection />
      <MetricsSection />
      <AboutSection />
      <ContactSection />
    </div>
  );
};

export default Home;
